import React, { Component } from 'react'
import axios from "axios"
import { API_URL } from "./constants/index.js"
import { Row, Col, Container } from 'react-bootstrap';

import GCOutput from "./GCOutput"
import { ThemeConsumer } from 'react-bootstrap/esm/ThemeProvider';

class GCInput extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value: '',
            isVisible: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleSubmit = e => {
        this.state.isVisible = true
        e.preventDefault()
        console.log(this.state)
        axios.post(API_URL, this.state)
            .then(response => {
                console.log(response)
                alert("Submitted")
            })
            .catch(error => {
                console.log(error)
            })
    }


    handleChange = e => {
        this.setState({ value: e.target.value });
    }

    render() { 
        return (
            <div>
                <p> Please enter your input DNA sequence (an array of A, T, G and C)</p>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <textarea
                            type="text" 
                            name="value"
                            style={{width:'350px', height:'250px'}}
                            placeholder="DNA sequence comprising of A, T, G and C"
                            value={this.state.value}
                            onChange={this.handleChange} 
                            rows="3"
                        />
                        <p><button type ="submit">Submit</button></p>
                    </div>
                </form>
                {console.log(this.state.isVisible)}
                { this.state.isVisible ? <GCOutput/> : null }
            </div>
        )
    }
}
export default GCInput
