import React, { Component } from 'react'
import axios from "axios"
import { API_URL } from "./constants/index.js"
import { Row, Col, Container } from 'react-bootstrap';

import GCOutput from "./GCOutput"

class GCInput extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value: '',
            sequences: []
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    //getOutput () {
    //    axios.get(API_URL)
    //        .then((response) =>  {
    //            console.log(response.data[response.data.length-1])
    //        })
    //}

    componentDidMount(){
        this.refreshList()
    }

    refreshList = () => {
        axios.get(API_URL)
            .then(res => this.setState({sequences: res.data}))

         console.log("SEQUENCESss",this.state.sequences)
    }


    handleSubmit = e => {
        e.preventDefault()
        console.log(this.state)
        axios.post(API_URL, this.state)
            .then(response => {
                console.log(response)
                alert("Submitted")
                this.refreshList()
            })
            .catch(error => {
                console.log(error)
            })
        //axios.get(API_URL)
        //    .then((response) =>  {
        //       console.log(response.data)
        //    })


    }


    handleChange = e => {
        this.setState({ value: e.target.value });
    }

    render() {
        return (
            <div>
                <p> Please enter your input DNA sequence (an array of A, T, G and C)</p>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <textarea
                            type="text" 
                            name="value"
                            style={{width:'350px', height:'250px'}}
                            placeholder="DNA sequence comprising of A, T, G and C"
                            value={this.state.value}
                            onChange={this.handleChange} 
                            rows="3"
                        />
                        <p><button type ="submit">Submit</button></p>
                    </div>
                </form>
                <GCOutput/>
            </div>
        )
    }
}
export default GCInput
