import React, { Component } from 'react'
import axios from "axios"
import { API_URL } from "src/constants/index.js"

class Input extends Component {
    constructor(props){
        super(props)
        this.state = {
            sequence: ''
        }
    }

    handleSubmit = (event) => {
        event.preventDefault()
        this.setState({
            [event.target.sequence]: event.target.value
        })
        const data = this.state
        console.log("final data is:", data)
    }

    handleInputChange = (event) => {
        event.preventDefault()
        console.log(event.target.name)
        console.log(event.target.value)
        this.setState({
            [event.target.sequence]: event.target.value
        })

    }

    componentDidMount() {
        if(this.props.sequence) {
            const { sequence } = this.props.sequence
            this.setState({sequence})
        }
    }

    createSequence = e => {
        e.preventDefault();
        axios.post(API_URL, this.state).then(() => {
            this.props.resetState()
            this.props.toggle()
        });
    }

    editSequence = e => {
        e.preventDefault()
        axios.put(API_URL + this.state.sequence, this.state).then(() => {
            this.props.resetState();
            this.props.toggle();
        });
    }

    render() {
        const {sequence} = this.state
        return (
            <div>
                <p> Please enter your input DNA sequence (an array of A, T, G and C)</p>
                <form onSubmit={this.props.sequence ? this.editSequence : this.createSequence}>
                    <p><textarea type='text' style={{width: "500px", height:"300px"}} 
                    placeholder='DNA Sequence comprising of A, T, G and C' 
                    sequence='sequence' onChange={this.handleInputChange} rows="3"></textarea></p>
                    <p><button>Submit</button></p>
                </form>
            </div>
        )
    }
}
export default Input
