import React, { Component } from 'react'

class Input extends Component {
    constructor(props){
        super(props)
        this.state = {
            sequence: null
        }
    }

    handleSubmit = (event) => {
        event.preventDefault()
        const data = this.state
        console.log("final data is:", data)
    }

    handleInputChange = (event) => {
        event.preventDefault()
        this.setState({
            [event.target.sequence]: event.target.value
        })

    }


    render() {
        const {sequence} = this.state
        return (
            <div>
                <p> Please enter your input DNA sequence (an array of A, T, G and C)</p>
                <form onSubmit={this.handleSubmit}>
                    <p><textarea type='text' style={{width: "500px", height:"300px"}} 
                    placeholder='DNA Sequence comprising of A, T, G and C' 
                    seq='sequence' onChange={this.handleInputChange} rows="3"></textarea></p>
                    <p><button>Submit</button></p>
                </form>
            </div>
        )
    }
}
export default Input
